import { Component } from '@angular/core';
import { Category } from '../categories';
import { categories } from '../categories';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent {
  categories = categories
}
<a [routerLink]="['/']">
<div class="title_name"><h2>Categories</h2></div>
</a>
    <div class="category-images">
  <div *ngFor="let category of categories" class="categoryy">
    <h3 [title]="category.category" [routerLink]="['/category', category.category]">
    <img [src]="category.image" alt="{{category.category}} image" class="category-image">
  </h3>
</div>
    </div>
