import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product, products } from '../products';
import { CartService } from '../cart.service';



@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit {
  product: Product | undefined;


  constructor(
    private route: ActivatedRoute,
    private cartService: CartService
  )
    { }

  ngOnInit() {
    // First get the product id from the current route.
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));

    // Find the product that correspond with the id provided in route.
    this.product = products.find(product => product.id === productIdFromRoute);
  }

  addToCart(product: Product){
    this.cartService.addToCart(product);
    window.alert('Your product has been added to the cart');
  }

}
<div class = "title"><h2>Product Details</h2></div>
<div class = "container">
<div *ngIf="product">
  <div class = "items">
  <h3>{{ product.name }}</h3>
  <h4>{{ product.price | currency }}</h4>
  <div class = "title_name">
  <img src = "{{product.image}}" alt = "Product from Kaspi" width = "250" height = "300" >
  <section class = "gallery">
    <div class = "container">
  <div class = "img-container">
    <div *ngFor="let image of product.images" >
      <img src={{image}} alt="product image">
    </div>
  </div>
    </div>
</section>
  </div> 
  <div class = "border_items">
  <p>{{ product.description }}</p>
  <div class = "buttons">
  <a href = "https://t.me/share/url?url={{product.link}}&text={text}"><img src = "https://clipground.com/images/telegram-app-logo-png-3.png" alt ="Telegram" height = "50" style = "margin-top: 20px;">  </a>
  <a href="https://api.whatsapp.com/send?phone=whatsappphonenumber&text={{product.link}}"><img src = "https://clipground.com/images/descargar-logo-de-whatsapp-png-2.png" alt = "Whatsapp" height = "50" style = "margin-left: 20px; margin-top: -50px"></a></div>
  <div class = "button"> <button type="button"(click)="addToCart(product)"> Buy </button> </div>
  </div>
  </div>
</div>
</div>
