})
export class ProductGalleryComponent {
  images = [
    'https://resources.cdn-kaspi.kz/shop/medias/sys_master/images/images/h27/h7e/46392662720542/apple-iphone-13-128gb-cernyj-102298404-2-Container.jpg',
    'https://resources.cdn-kaspi.kz/shop/medias/sys_master/images/images/hc8/h10/46392663310366/apple-iphone-13-128gb-cernyj-102298404-3-Container.jpg',
    'https://example.com/image3.jpg'
  ];
}
